"""vBond configuration for Viptela supported platforms

This file provides access to the vBond API implementation found in the
pnp_platform module. If the API implementation is not found, it is not
guaranteed that a PnP Device will connect to a vBond controller.

"""
from pnp.infra.api.pnp_infra import PnPInfra
try:
    from pnp_platform.infra.vbond import VBond
    if not issubclass(VBond, PnPInfra):
        raise ImportError("vBond implementation does not conform to PnPInfra "
                          "API")
except ImportError:
    from pnp.infra.api.vbond import VBond
