"""PnP File Paths

Loads file paths from file paths config file to filepaths dictionary. Dictionary
structured so that top-level key is config section and second-level key is
config option within the section.

Note:
    If a platform specifies a 'PLATFORM_FILE_PATHS_CONFIG' value in
    pnp_platform.infra.constants, then the file paths configured in this
    platform file will overwrite the same file paths config fields provided by
    default in platform independent behavior.

"""
from ConfigParser import SafeConfigParser
from logging import getLogger

from pnp.infra.utils.pnp_constants import PNP_FILE_PATHS_CONFIG

filepaths = {}
config = SafeConfigParser()
config.read(PNP_FILE_PATHS_CONFIG)
for section in config.sections():
    filepaths[section] = {}
    for option in config.options(section):
        filepaths[section][option] = config.get(section, option).strip('\" \'')

getLogger(__name__).debug("Loaded PnP file paths from config")
