""" Exec Log Info

This file contains the Exec Log Info base class for collecting execution
log info that are used by PnP infra, services, etc.

"""

class ExecLogInfo(object):
    """ExecLogInfo base class
    """
    __output_log = None
    __error_log = None

    @property
    def output_log(self):
        """str: This is the execution output log
        """
        return self.__output_log

    @output_log.setter
    def output_log(self, log_):
        """This is the setter for output_log
        """
        self.__output_log = log_

    @property
    def error_log(self):
        """str: This is the execution error log
        """
        return self.__error_log

    @error_log.setter
    def error_log(self, log_):
        """This is the setter for error_log
        """
        self.__error_log = log_
